import { SliderData } from './SliderData'
import './css/Slider.css'

const Slider = () => {
    const [current, setCurrent] = useState(0);
    const length = SliderData.length; //slides

  return (
    <section className='slider'>
        {SliderData.map((slide, index) =>{
            return (
                <div>
                   
                    <img src={slide.image} alt="imgs" />
                    <i style={{color: 'red'}} class="fa-solid fa-angle-left" id="slider_i" className='left-arrow'></i>
                    <i style={{color: 'red'}} class="fa-solid fa-angle-right" id="slider_i" className=''></i>
                    
                </div>
                
                
            )
        })}
    </section>
  )
}

export default Slider
