import React, {useState} from 'react'
import { SliderData } from './SliderData'
import './css/Slider.css'

const Slider = ({slides}) => {
    const [current, setCurrent] = useState(0);
    const length = slides.length; //slides

  return (
    <section className='slider'>

                   

        {SliderData.map((slide, index) =>{
            return (
                <div>
                   
                    <img src={slide.image} alt="imgs" />
                    <i class="fa-solid fa-angle-left" id="slider_i" className='left-arrow'></i>
                    <i class="fa-solid fa-angle-right" id="slider_i" className='right-arrow'></i>
                    
                    <p id="slider_i">1</p>
                   
                </div>
                
                
            )
        })}
    </section>
  )
}

export default Slider
